				case 'intro':
					return <Introduction />;
				case 'new game':
					return <NewGame />;
				default:
					return <p>Nothing to see here.</p>
			}
		}
		return <div>
			{pageContent()}
		</div>
	}


	return (
		<Provider store={store}>
			{pageContent}



