import React, { useState } from 'react';
import { CSSTransition } from 'react-transition-group';
import { CreatePlayer } from './Player';
import Introduction from './components/Introduction';
import NewGame from './components/NewGame';
import Game from './components/Game';
import FreePlayerGame from './components/FreePlayerGame';
import Announcer from './components/Announcer';
import './style/App.css';

import { Provider } from "react-redux";
import store from "../src/redux/store";
import { useSelector, useDispatch } from "react-redux";
import { changeAppStatus } from './redux/actions';


const App = () => {

	const [appStatus, setAppStatus] = useState('intro');
	const [winner, setWinner] = useState(null);
	let PlayerOne = CreatePlayer('human-player', 'human');
	let PlayerTwo = CreatePlayer('ai-player', 'computer');
	//update timeout from 2000 to 20//
	// let timeout = 20;
	const newAppStatus = useSelector(state => state.newAppStatus);
	const dispatch = useDispatch();


	return (
		<Provider store={store}>
			<div>
				<CSSTransition>
					<Introduction />
				</CSSTransition>
				<CSSTransition
					in={appStatus === 'newGame'}
					// timeout={timeout}
					// classNames="trans"
					unmountOnExit={true}
				>
					<NewGame/>
				</CSSTransition>

				<CSSTransition
					in={appStatus === 'game'}
					timeout={timeout}
					classNames="trans"
					unmountOnExit={true}
				>
					<Game
						player={PlayerOne}
						opponent={PlayerTwo}
						setWinner={setWinner}
						winner={winner}
						setAppStatus={setAppStatus}
					/>
				</CSSTransition>

				{/* freeplayergame */}
				<CSSTransition
					in={appStatus === 'freeplayergame'}
					timeout={timeout}
					classNames="trans"
					unmountOnExit={true}
				>
					<FreePlayerGame
						player={PlayerOne}
						opponent={PlayerTwo}
						setWinner={setWinner}
						winner={winner}
						setAppStatus={setAppStatus}
					/>
				</CSSTransition>

				<CSSTransition
					in={appStatus === 'announcer'}
					timeout={timeout}
					classNames="trans"
					unmountOnExit={true}
				>
					<Announcer winner={winner} setAppStatus={setAppStatus} />
				</CSSTransition>
			</div>
		</Provider>
	);
};

export default App;
